	public int getMaximumSubset(String[] S)
	{
        //ArrayList<String> f = new ArrayList<String>();
		HashSet<String> f = new HashSet<String>();
        for (String st : S)
        {
            char[] a = st.toCharArray();
            Arrays.sort(a);
            String b = new String(a);
            //if (!f.contains(b)) 
            	f.add(b);
        }
        return f.size();
	}
	
