	public int countLayouts(String bricks)
	{
//		ArrayList<Character> hs = new ArrayList<Character>();
//		for (char c : bricks.toCharArray())
//			if (!hs.contains(c))		
//			hs.add(c);
		
		HashSet<Character> hs = new HashSet<Character>();
		for (char c : bricks.toCharArray())
			hs.add(c);
		
		int cnt = hs.size();
		if (cnt == 1 || cnt == 2)
			return cnt;
		return 0;
	}
	
