	public long totalDamage(int T)
	{
		long sum = 1, gm = 1, g = 1;
		for (gm = 2; gm <= T; gm++) {
			g += gm;
			sum += (g + ((2+g +gm-2) * (gm-1))) ;
			//System.out.println(gm + " " + sum);
		}
		return sum;
	}
	
